import Layout from "../components/MyLayout";

const buttonStyle = {
  color: "white",
  backgroundColor: "green",
  padding: "0.5rem",
  borderRadius: "10px",
  fontSize: "1.5rem"
};

const inputStyle = {
  backgroundColor: "#ffffff",
  borderRadius: "4px",
  border: "1px solid #E0E0E0",
  boxShadow: "inset 0 1px 2px #FAFAFA",
  maxWidth: "80%",
  fontSize: "1.5rem"
};

export class Start extends React.Component {
  state = {
    firstName: "",
    lastName: "",
    parentType: "",
    phone: "",
    email: ""
  };

  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1
    });
  };

  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1
    });
  };

  handleChange = input => e => {
    this.setState({ [input]: e.target.value });
  };

  render() {
    const { firstName, lastName, parentType, phone, email } = this.state;
    const values = { firstName, lastName, parentType, phone, email };
    return (
      <Layout>
        <form>
          <div>
            <label htmlFor="firstName">First Name </label>
            <input
              style={inputStyle}
              id="firstName"
              type="text"
              placeholder="John"
              value={values}
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="lastName">Last Name </label>
            <input
              style={inputStyle}
              id="lastName"
              type="text"
              placeholder="Smith"
              value={values}
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label htmlFor="parent">Parent </label>
            <input
              style={inputStyle}
              id="parent"
              type="text"
              value={values}
              onChange={this.handleChange}
              placeholder="Mom/Dad"
            />
          </div>
          <div>
            <label htmlFor="phone">Phone </label>
            <input
              style={inputStyle}
              id="phone"
              type="text"
              value={values}
              onChange={this.handleChange}
              placeholder="4175551234"
            />
          </div>
          <div>
            <label htmlFor="email">Email </label>
            <input
              style={inputStyle}
              id="email"
              type="text"
              value={values}
              onChange={this.handleChange}
              placeholder="awesome-parent@gmail.com"
            />
          </div>
          <div style={{ margin: `1rem auto`, textAlign: "center" }}>
            <button style={buttonStyle} type="submit" value="Submit">
              Submit
            </button>
          </div>
        </form>
      </Layout>
    );
  }
}

export default Start;
